// @ts-check
import React, { Component } from 'react';
import { connect } from 'react-redux';

import { userActions } from '../_actions';
import {Grid, Typography, TextField, Button} from '@material-ui/core';
import {RegisterLink} from '../_components';

class LoginPage extends Component {
    /** @param {any} props */
    constructor(props) {
        super(props);

        // reset login status
        this.props.dispatch(userActions.logout());

        this.state = {
            username: '',
            password: '',
            submitted: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    /** @param {any} e */
    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }

    /** @param {any} e */
    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        const { dispatch } = this.props;
        if(username && password) {
            dispatch(userActions.login(username, password));
        }
    }

    render() {
        const { loggingIn } = this.props;
        const { username, password } = this.state;
        return (
            <Grid>
                <Typography component="h2">Login</Typography>
                <form name="form" noValidate autoComplete="off" onSubmit={this.handleSubmit}>
                    <TextField
                        id="standard-name"
                        label="Username"
                        name="username"
                        value={username}
                        onChange={this.handleChange}
                        margin="normal"
                        fullWidth
                        required
                    />
                    <TextField
                        id="outlined-password-input"
                        label="Password"
                        type="password"
                        name="password"
                        value={password}
                        onChange={this.handleChange}
                        autoComplete="current-password"
                        margin="normal"
                        fullWidth
                        required
                    />
                    <Button variant="contained" color="primary" type="submit">Login</Button>
                    {
                        loggingIn &&
                        <img alt="logging in" src="data:image/gif;base64," />
                    }
                    <Button component={RegisterLink}>Register</Button>
                </form>
            </Grid>
        );
    }
}

/** @param {any} state */
function mapStateToProps(state) {
    const { loggingIn } = state.authentication;
    return {
        loggingIn
    };
}

const connectedLoginPage = connect(mapStateToProps)(LoginPage);
export { connectedLoginPage as LoginPage };
